import { useForm } from "react-hook-form";
import { useState } from 'react';
import { yupResolver } from '@hookform/resolvers/yup';
import * as yup from "yup";
import 'react-toastify/dist/ReactToastify.css';

const schema = yup.object().shape({
    name: yup.string().required(),
    email: yup.string().email().required(),
    content: yup.string().required()
});

function Opiniao() {
    const {
        register,
        handleSubmit,
        formState: { errors },
        reset,
    } = useForm({
        resolver: yupResolver(schema),
    });

    const [showSuccessMessage, setShowSuccessMessage] = useState(false);

    const onSubmitForm = (data, e) => {
        e.preventDefault();
        console.log(data);
        reset();
        alert(`Nome: ${data.name}\nEmail: ${data.email}\nConteúdo: ${data.content}`);
    };

    return (
        <>
            <div className="bg-dark">
                <h1 className="text-white pt-5 text-center">Leave your opinion about the series</h1>
                <div className="formu">
                    <form onSubmit={handleSubmit(onSubmitForm)}>
                        <div className='field'>
                            <label className='label'>Name:</label>
                            <input type='text' {...register('name')} />
                            <span>{errors.name?.message}</span>
                        </div>
                        <div className='field'>
                            <label className='label'>Email:</label>
                            <input type='email' {...register('email')} />
                            <span>{errors.email?.message}</span>
                        </div>
                        <div className='field pb-5'>
                            <label className='label'>Comment:</label>
                            <input type='text' {...register('content')} />
                            <span>{errors.content?.message}</span>
                        </div>
                        <button className="rounded bg-dark border-0" type='submit'>Post</button>
                    </form>
                </div>
            </div>
        </>
    );
}

export default Opiniao;
